import { Typography  } from "@mui/material";
import React, { useRef } from "react";
import emailjs from '@emailjs/browser';



const Chat = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_z83wjzk', form.current, 'IdxkysMK1LwB52jll')
      .then((result) => {
         window.alert('Sent');
      }, (error) => {
        window.alert('Fail');
      });
  };
  
  return (
    <form method="GET" ref={form} onSubmit={sendEmail} >
      <Typography>
      <div className='contactusDiv'>

        <div className="contactbox">
      <div className="formdiv">
        <div className='getintouch'>
          <h2>Get In Touch</h2>
        </div>
       <div className='contactus'>     
        <input type="text" id="name" name="name" className="inputContact" placeholder="Name"  required />
       </div>
      <div className="contactus">
        
        <input type="email" id="email"  name="email" className="inputContact" placeholder="Email"  required />
      </div>
      <div className="contactus">
        
        <input type="phone" id="phone" name="phone" className="inputContact" placeholder="Phone"  required />
      </div>
      <div className="contactus">
      
        <textarea id="message" name="message" className="inputContact" placeholder="Message"  required />
      </div>
      <div className="contactbutton">
      <button  type="submit" value="Send"><Typography>Submit</Typography></button>
      </div>
      </div>




     
      <img alt="img" className="contactimg" src="https://www.travelyourwaydirect.com/oshutoon/2022/03/contact-us.png" />
      </div>
      </div>
        
      </Typography>
    </form>
  );
};

export default Chat;
