import { Typography  } from "@mui/material";
import React, { useState } from "react";


const Chat = () => {
  const [status, setStatus] = useState("Submit");
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    const { name, email, message } = e.target.elements;
    let details = {
      name: name.value,
      email: email.value,
      message: message.value,
    };
    let response = await fetch("/chat", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status); 
  };
  
  return (
    <form onSubmit={handleSubmit}>
      <Typography>
      <div className='contactusDiv'>
      <div className="formdiv">
        <div className='getintouch'>
          <h2>Get In Touch</h2>
        </div>
       <div className='contactus'>     
        <input type="text" id="name" className="inputContact" placeholder="Name" required />
       </div>
      <div className="contactus">
        
        <input type="email" id="email"  className="inputContact" placeholder="Email" required />
      </div>
      <div className="contactus">
        
        <input type="phone" id="phone" className="inputContact" placeholder="Phone" required />
      </div>
      <div className="contactus">
      
        <textarea id="message" className="inputContact" placeholder="Message" required />
      </div>
      <div className="contactbutton">
      <button  type="submit"><Typography>{status}</Typography></button>
      </div>
      </div>




     <div className="formdiv">
      <img alt="img" className="contactimg" src="https://www.travelyourwaydirect.com/oshutoon/2022/03/contact-us.png" />
     </div>
      </div>
        
      </Typography>
    </form>
  );
};

export default Chat;
